board = [list(line) for line in input.strip().split("\n")]
width = len(board[0])

empty_row_indices = set([])
for c, row in enumerate(board):
    if all(x == "." for x in row):
        empty_row_indices.add(c)
empty_column_indices = set([])
for c in range(width):
    if all(row[c] == "." for row in board):
        empty_column_indices.add(c)

galaxy_positions = [(r, c) for r in range(len(board))
                    for c in range(width) if board[r][c] == "#"]

galaxy_pairs = [(galaxy_positions[i], galaxy_positions[j])
                for i in range(len(galaxy_positions)) for j in range(i + 1, len(galaxy_positions))]


total = 0

factor = 1000000 - 1

for a, b in galaxy_pairs:
    min_row = min(a[0], b[0])
    max_row = max(a[0], b[0])
    min_column = min(a[1], b[1])
    max_column = max(a[1], b[1])
    rows_passed_through = [r for r in range(min_row, max_row + 1)
                           if r in empty_row_indices]
    columns_passed_through = [c for c in range(min_column, max_column + 1)
                              if c in empty_column_indices]
    distance = (max_row - min_row) + (max_column - min_column) + \
        len(rows_passed_through) * factor + \
        len(columns_passed_through) * factor
    total += distance
print(total)
